import React, { Component } from 'react';
import axios from 'axios';
import { Form, Button, Container, Alert } from 'react-bootstrap';
import ServicoDeCambio from '../ServicoDeCambio.js';

class Converter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      moedas: [],
      valor: '',
      moedaDe: 'USD',
      moedaPara: 'EUR',
      resultado: null,
      erro: null,
    };
  }

  componentDidMount() {
    ServicoDeCambio.obterMoedas()
      .then(response => {
        this.setState({ moedas: Object.keys(response.data.conversao_moedas) });
      })
      .catch(error => this.setState({ erro: 'Erro ao carregar moedas.' }));
  }

  converter = () => {
    ServicoDeCambio.converterMoeda(this.state.moedaDe, this.state.moedaPara)
      .then(response => {
        const taxa = response.data.conversao_moedas[this.state.moedaPara];
        this.setState({ resultado: this.state.valor * taxa });
      })
      .catch(error => this.setState({ erro: 'Erro na conversão de moeda.' }));
  };

  render() {
    const { moedas, valor, moedaDe, moedaPara, resultado, erro } = this.state;
    return (
      <Container className="mt-5">
        <h1>Converter Moeda</h1>
        <Form>
          <Form.Group controlId="valor">
            <Form.Label>Valor</Form.Label>
            <Form.Control
              type="number"
              value={valor}
              onChange={(e) => this.setState({ valor: e.target.value })}
              placeholder="Digite o valor"
            />
          </Form.Group>
          <Form.Group controlId="moedaDe">
            <Form.Label>Moeda de</Form.Label>
            <Form.Select
              value={moedaDe}
              onChange={(e) => this.setState({ moedaDe: e.target.value })}
            >
              {moedas.map(moeda => (
                <option key={moeda} value={moeda}>{moeda}</option>
              ))}
            </Form.Select>
          </Form.Group>
          <Form.Group controlId="moedaPara">
            <Form.Label>Moeda para</Form.Label>
            <Form.Select
              value={moedaPara}
              onChange={(e) => this.setState({ moedaPara: e.target.value })}
            >
              {moedas.map(moeda => (
                <option key={moeda} value={moeda}>{moeda}</option>
              ))}
            </Form.Select>
          </Form.Group>
          <Button variant="primary" onClick={this.converter} className="mt-3">Converter</Button>
        </Form>
        {erro && <Alert variant="danger" className="mt-3">{erro}</Alert>}
        {resultado !== null && (
          <div className="mt-3">
            <h4>Resultado: {resultado.toFixed(2)}</h4>
          </div>
        )}
      </Container>
    );
  }
}

export default Converter;
